namespace Resido.Model.TTLockDTO.RequestDTO.FingerPrintRq
{
    /// <summary>
    /// Client-facing request DTO (no access token).
    /// </summary>
    public class DeleteFingerprintRequestDTO
    {
        /// <summary>
        /// Lock ID, generated by Lock init.
        /// </summary>
        public int LockId { get; set; }

        /// <summary>
        /// Fingerprint ID to delete (from AddFingerprint response).
        /// </summary>
        public int FingerprintId { get; set; }

        /// <summary>
        /// Method of deletion (1 = Bluetooth, 2 = Cloud).
        /// </summary>
        public OperationType DeleteType { get; set; } = OperationType.Bluetooth;
    }

    /// <summary>
    /// TTLock API request DTO (includes access token, clientId, date).
    /// </summary>
    public class TTLockDeleteFingerprintRequestDTO : BaseRequestDTO, IAccessTokenRequest
    {
        public int LockId { get; set; }
        public int FingerprintId { get; set; }
        public int DeleteType { get; set; }
        public string AccessToken { get; set; }
    }

}
