namespace Resido.Model.TTLockDTO.RequestDTO.LockSettingRq
{
    /// <summary>
    /// Client-facing request DTO (no access token).
    /// </summary>
    public class ModifyLockSettingsRequestDTO
    {
        /// <summary>
        /// Lock ID, generated by Lock init.
        /// </summary>
        public int LockId { get; set; }

        /// <summary>
        /// Setting type (2 = Privacy lock, 3 = Tamper alert, etc.).
        /// </summary>
        public LockSettingType Type { get; set; }

        /// <summary>
        /// Value depends on type:
        /// - 1 = on, 2 = off
        /// - Open direction: 1 = left, 2 = right
        /// - Sound volume: 1–5
        /// - Door unclosed alarm time: seconds (0 = off, requires door sensor)
        /// </summary>
        public int Value { get; set; }

        /// <summary>
        /// Method of change (1 = Bluetooth, 2 = Cloud).
        /// </summary>
        public OperationType ChangeType { get; set; } = OperationType.Bluetooth;
    }

    /// <summary>
    /// TTLock API request DTO (includes access token, clientId, date).
    /// </summary>
    public class TTLockModifyLockSettingsRequestDTO : BaseRequestDTO, IAccessTokenRequest
    {
        public int LockId { get; set; }
        public int Type { get; set; }
        public int Value { get; set; }
        public int ChangeType { get; set; }
        public string AccessToken { get; set; }
    }
}
