namespace Resido.Model.TTLockDTO.RequestDTO.LockSettingRq
{
    /// <summary>
    /// Client-facing request DTO (no access token).
    /// </summary>
    public class UpdateLockDataRequestDTO
    {
        /// <summary>
        /// Lock ID, generated by Lock init.
        /// </summary>
        public int LockId { get; set; }

        /// <summary>
        /// Renewed lock data from APP SDK (after reset ekey/passcode or feature change).
        /// </summary>
        public string LockData { get; set; }
    }

    /// <summary>
    /// TTLock API request DTO (includes access token, clientId, date).
    /// </summary>
    public class TTLockUpdateLockDataRequestDTO : BaseRequestDTO, IAccessTokenRequest
    {
        public int LockId { get; set; }
        public string LockData { get; set; }
        public string AccessToken { get; set; }
    }
}
